mapper.select:
select * from information_class


        Cid = #{Cid}


        and Cid in (select cid from cse.message_information_class where Mid = #{RelativeMessage})

mapper.update:
update information_class
Name = #{name}, Resume = #{resume}, BasicMessage = #{basicMessage}, Location = #{location}, Type = #{type}
where Cid = #{Cid};
